using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Design;using Microsoft.Extensions.Configuration;using System.IO;namespace SnackShop.Models{	public class SnackShopContextFactory : IDesignTimeDbContextFactory<SnackShopContext>	{		SnackShopContext IDesignTimeDbContextFactory<SnackShopContext>.CreateDbContext(string[] args)		{			IConfigurationRoot configuration = new ConfigurationBuilder()				.SetBasePath(Directory.GetCurrentDirectory())				.AddJsonFile("appsettings.json")				.Build();						var builder = new DbContextOptionsBuilder<SnackShopContext>();			var connectionString = configuration.GetConnectionString("DefaultConnection");			builder.UseMySql(connectionString);			return new SnackShopContext(builder.Options);		}	}}
